sysauto_script.script:
var all_users = new GlideRecord("sys_user");

//Test Query/Data
encQuery = 'GOTOuser_nameLIKETERM1';

all_users.addEncodedQuery(encQuery);
all_users.query();

while (all_users.next()) {

	var sys_id = all_users.sys_id;
	var name = all_users.name;
	var employee_number = all_users.user_name;
	var email = all_users.email;

	var Cart = new sn_sc.CartJS();

	var newItem = Cart.addToCart({

		"sysparm_id": "cbde195f2f4431100936fe7cf699b643",
		"sysparm_quantity": "1",
		"sysparm_requested_for": sys_id.toString(),
		"variables": {

			"name": name.toString(),
			"employee_number": employee_number.toString(),
			"email": email.toString()
		}
	});

	Cart.checkoutCart();


}
